import React, { useEffect, useState } from "react";
import { SearchResult } from "./SearchResult";
import { db } from '../firebase';
import {collection, onSnapshot, query, QuerySnapshot, where} from "firebase/firestore"


export const Search = () => {
  const [barcode, setBarcode] = useState("");
  const [data, setData] = useState([]);
  const [errors, setErrors] = useState("");
  const [exists, setExists] = useState(false);
  const [product, setProduct] = useState();

  useEffect(() =>{
    if(exists){
      addProductFirebase(product);
    }
    // eslint-disable-next-line
  },[product]);

  useEffect(() =>{
    if(exists){
      addProductFirebase(product);
    }
    // eslint-disable-next-line
  },[errors]);

  const addProduct = (productObject) =>{
    const q = query(collection(db, "products"), where('barcode', '==', productObject.barcode));
    onSnapshot(q, (querySnapshot) => {
      if (querySnapshot.empty){
        setExists(true);
        setProduct(productObject)
      } else{
        setExists(false);
        console.log("product already listed on program");
      }
    });    
  };

  const addProductFirebase = async (product) =>{
    await db.collection('products').doc().set(product);
    console.log('product added');
  }

  const apiRequest = (e) => {
    e.preventDefault();

    let datos = e.target;
    let search = datos.barcode.value;
    setBarcode(search);

    setTimeout(async () => {
        try{
            const request = await fetch(
                "https://world.openfoodfacts.org/api/v0/product/" + search + ".json"
              );
              const { product } = await request.json();
        
              setData(product);
              setErrors("");
              console.log(errors)
        } catch(error){
            setErrors(error.message);
            console.log(errors)
        }
      
    }, 1000);
  };

  if(errors !== "") {
    return (
      <div>
        <p>Enter a barcode to search the product</p>
        <form className="card card-body" onSubmit={apiRequest}>
          <div className="form-group input-group">
            <input
              className="form-control"
              type="text"
              name="barcode"
              placeholder="8436048475739"
            />
            &nbsp;
            <input
              className="btn btn-primary btn-block"
              type="submit"
              value="Search product"
            />
          </div>
        </form>

        <br></br>
        <p>Enter a valid barcode</p>
      </div>
    );
  } else if (data.product_name) {
    return (
      <div>
        <p>Enter a barcode to search the product</p>
        <form className="card card-body" onSubmit={apiRequest}>
          <div className="form-group input-group">
            <input
              className="form-control"
              type="text"
              name="barcode"
              placeholder="8436048475739"
            />
            &nbsp;
            <input
              className="btn btn-primary btn-block"
              type="submit"
              value="Search product"
            />
          </div>
        </form>

        <br></br>
        <SearchResult data={data} addProduct={addProduct}/>
      </div>
    );
  } else if (barcode === "") {
    return (
      <div>
        <p>Enter a barcode to search the product</p>
        <form className="card card-body" onSubmit={apiRequest}>
          <div className="form-group input-group">
            <input
              className="form-control"
              type="text"
              name="barcode"
              placeholder="8436048475739"
            />
            &nbsp;
            <input
              className="btn btn-primary btn-block"
              type="submit"
              value="Search product"
            />
          </div>
        </form>
      </div>
    );
  }
};
