import React, { useEffect, useState } from 'react'
import { Rating } from 'react-simple-star-rating'

export const SearchResult = ({data, barcode, keywords, addProduct}) => {
    const [rating, setRating] = useState(0); 
    const [product, setProduct] = useState({
        barcode: barcode,
        product_name: data.product_name,
        keywords: keywords,
        image: data.image_url,
        total_rate: 0,
        rate: rating,
        num_rates: 1
    });

    useEffect(() =>{
        setProduct({
            barcode: barcode,
            product_name: data.product_name,
            keywords: keywords,
            image: data.image_url,
            total_rate: 0,
            rate: rating,
            num_rates: 1
        });
        // eslint-disable-next-line
    },[keywords]);

    //handle rating and save on firebase
    const handleRating = (rating) => {
        setRating(rating);
        //update product rating by user
        setProduct({...product, total_rate: product.total_rate + rating, rate: rating});
    };    

    const publishProduct = (e) => {
        e.preventDefault();
        addProduct(product);
    };

    if(data.image_url){
        return (
            <div className="col-md-16">
                    <div className="card mb-1">
                        <div className="card-body">
                            <div>
                                <img className="product-image" src={data.image_url} alt={data.product_name}></img>
                            </div>
                            <div className="product-data">
                                <p> <strong>Barcode:</strong> {barcode}</p>
                                <p> <strong>Name:</strong> {data.product_name} </p>
                                <p> <strong>Keywords:</strong> {keywords + ""} </p>
                                <Rating
                                    onClick={handleRating}
                                    ratingValue={rating}
                                    size={20}
                                    label
                                    transition
                                    fillColor='orange'
                                    emptyColor='gray'
                                    className='stars'
                                />
                                <form className='publish' onSubmit={publishProduct}>
                                <input
                                    className="btn btn-primary btn-block"
                                    type="submit"
                                    value="Publish product"
                                />
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
        );
    } else{
        return (
            <div className="col-md-16">
                    <div className="card mb-1">
                        <div className="card-body">
                            <div className="product-data-no-image">
                                <p> <strong>Barcode:</strong> {barcode}</p>
                                <p> <strong>Name:</strong> {data.product_name} </p>
                                <p> <strong>Keywords:</strong> {keywords + ""} </p>
                                <Rating
                                    onClick={handleRating}
                                    ratingValue={rating}
                                    size={20}
                                    label
                                    transition
                                    fillColor='orange'
                                    emptyColor='gray'
                                    className='stars'
                                />
                                <form className='publish' onSubmit={publishProduct}>
                                <input
                                    className="btn btn-primary btn-block"
                                    type="submit"
                                    value="Publish product"
                                />
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
        );
    }
    
}
