import React, { useEffect, useState } from "react";
import { db } from "../firebase";
import { Rating } from "react-simple-star-rating";

export const TopProducts = () => {
  const [currentId, setCurrentId] = useState('');
  const [products, setProducts] = useState([]);
  const [rating, setRating] = useState(0);
  const [product, setProduct] = useState();

  useEffect(() => {
    getProducts();
  }, []);

  useEffect((currentId, product) => {
    if(currentId !== ''){
      updateProduct(currentId);
    } else{
      console.log('No object to update');
    }
  }, [product]);

  const getProducts = () => {
    db.collection("products").orderBy('rate', 'desc').onSnapshot((querySnapshot) => {
      const docs = [];
      querySnapshot.forEach((doc) => {
        docs.push({ ...doc.data(), id: doc.id });
      });
      setProducts(docs);
    });
  };

  const handleRating = (rating) => {
    setRating(rating);
  };

  const updateProduct = async (id) =>{
    await db.collection('products').doc(id).update(product);
    console.log('product with '+ id + ' updated succesfully');
  };

  return (
    <div className="col-md-16">
      {products.slice(0,5).map((product) => (
        <div className="card mb-1" key={product.id}>
          <div className="card-body">
            <div>
              <img
                className="product-image"
                src={product.image}
                alt={product.product_name} 
              ></img>
            </div>
            <div className="product-data">
            <p>
                {" "}
                <strong>Name:</strong> {product.product_name}{" "}
              </p>
              <p>
                {" "}
                <strong>Keywords:</strong> {product.keywords + ""}{" "}
              </p>
              <p>
                {" "}
                <strong>Global rating:</strong> {product.rate + ""}{" "}
              </p>
              <p>
                {" "}
                <strong>Number of rates:</strong> {product.num_rates + ""}{" "}
              </p>

              <Rating
                onClick={(rating) =>{
                  handleRating(rating);
                  setCurrentId(product.id);
                  setProduct({
                    barcode: product.barcode,
                    product_name: product.product_name,
                    keywords: product.keywords,
                    image: product.image,
                    total_rate: product.total_rate + rating,
                    rate: (product.rate + rating) / product.num_rates,
                    num_rates: product.num_rates + 1,
                  });
                }}
                ratingValue={rating}
                size={20}
                label
                transition
                fillColor="orange"
                emptyColor="gray"
                className="stars"
              />
            </div>
          </div>
        </div>
      ))}
    </div>
  );

};
