import React, { useEffect, useState } from 'react'
import { Rating } from 'react-simple-star-rating'
import { db } from '../firebase';

export const Product = ({data, barcode, keywords}) => {
    const [rating, setRating] = useState(0); 
    const [product, setProduct] = useState({
        barcode: barcode,
        product_name: data.product_name,
        keywords: keywords,
        image: data.image_url,
        rate: rating,
        num_rates: 1
    });

    useEffect(() =>{
        //console.log(product);
    },[rating]);

    //handle rating and save on firebase
    const handleRating = (rating) => {
        setRating(rating);
        setProduct({...product, rate: (product.rate + rating) / product.num_rates, num_rates: product.num_rates + 1});
    };

    const addOrEdit = async (productObject) =>{
        await db.collection('products').doc().set(productObject);
        console.log('product added to firestore');
    };
    
    return (
    <div className="col-md-16">
            <div className="card mb-1">
                <div className="card-body">
                    <div>
                        <img className="product-image" src={data.image_url} alt={data.product_name}></img>
                    </div>
                    <div className="product-data">
                        <p> <strong>Barcode:</strong> {barcode}</p>
                        <p> <strong>Name:</strong> {data.product_name} </p>
                        <p> <strong>Keywords:</strong> {keywords + ""} </p>
                        <Rating
                            onClick={handleRating}
                            ratingValue={rating}
                            size={20}
                            label
                            transition
                            fillColor='orange'
                            emptyColor='gray'
                            //name='rate'
                        />
                        {/* Use rating value */}
                        <p className='rating'>{rating}</p>
                        <form className='publish' onSubmit={addOrEdit}>
                            <input
                                className="btn btn-primary btn-block"
                                type="submit"
                                value="Publish product"
                            />
                        </form>
                    </div>
                </div>
            </div>
        </div>
  )
}
