import React, { useState } from 'react'
import Rating from 'react-simple-star-rating'

export const RatingForm = () => {
  const [rating, setRating] = useState(0);

  const handleRating = (rate) => {
    setRating(rate)
  }

  return (
    <div className='App'>
      <Rating
        onClick={handleRating}
        ratingValue={rating}
        size={20}
        label
        transition
        fillColor='yellow'
        emptyColor='gray'
      />
      {rating}
    </div>
  )
}
