import React, { useEffect, useState } from "react";

export const ProductComponent = () => {
  const [data, setData] = useState([]);
  const [keywords, setKeywords] = useState([]);
  const [errors, setErrors] = useState("");

  const productSearch = () => {
    setTimeout(async () => {
      try {
        const request = await fetch('https://world.openfoodfacts.org/api/v0/product/${barcode}.json');
        const { product } = await request.json();

        setData(product);
        setKeywords(product._keywords);
      } catch (error) {
        setErrors(error.message);
      }
    }, 1000);
  };

  if (errors === "") {
    return (
      <div>
        ProductComponent
        <br></br>
        <img src={data.image_url} width="80"></img>
        <p> Name: {data.product_name} </p>
        <p> Keywords: {keywords + ""} </p>
      </div>
    );
  } else if (errors !== "") {
    return (
      <div>
        Enter a barcode to search the product
        <br></br>
        <p>Cannot find any product with this barcode</p>
      </div>
    );
  }
};
