import React, { useEffect, useState } from "react";
import { db } from "../firebase";
import { Rating } from "react-simple-star-rating";

export const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [rating, setRating] = useState(0);
  const [product, setProduct] = useState({
    barcode: products.barcode,
    product_name: products.product_name,
    keywords: products.keywords,
    image: products.image_url,
    rate: products.rating,
    num_rates: products.num_rates,
  });

  useEffect(() => {
    getProducts();
  }, []);

  const getProducts = () => {
    db.collection("products").onSnapshot((querySnapshot) => {
      const docs = [];
      querySnapshot.forEach((doc) => {
        docs.push({ ...doc.data(), id: doc.id });
      });
      setProducts(docs);
    });
  };


  const handleRating = (rating) => {
    setRating(rating);
    setProduct({
      ...product,
      rate: (product.rate + rating) / product.num_rates,
      num_rates: product.num_rates + 1,
    });
  };

  return (
    <div className="col-md-16">
      {products.map((product) => (
        <div className="card mb-1" key={product.id}>
          <div className="card-body">
            <div>
              <img
                className="product-image"
                src={product.image}
                alt={product.product_name}
              ></img>
            </div>
            <div className="product-data">
              <p>
                {" "}
                <strong>Barcode:</strong> {product.barcode}
              </p>
              <p>
                {" "}
                <strong>Name:</strong> {product.product_name}{" "}
              </p>
              <p>
                {" "}
                <strong>Keywords:</strong> {product.keywords + ""}{" "}
              </p>

              <Rating
                onClick={handleRating}
                ratingValue={rating}
                size={20}
                label
                transition
                fillColor="orange"
                emptyColor="gray"
                className="stars"
              />
              {/* Use rating value */}
              <p className="rating">{rating}</p>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};
