import React, { useEffect, useState } from 'react'

export const Search = () => {

const [barcode, setBarcode] = useState();
const [data, setData] = useState([]);
const [keywords, setKeywords] = useState([]);

const apiRequest = e => {

    e.preventDefault();

    let datos = e.target;
    let search = datos.barcode.value
    setBarcode(search);
    
    setTimeout(async() => {
            const request = await fetch("https://world.openfoodfacts.org/api/v0/product/"+search+ ".json");
            const {product} = await request.json();
        
            setData(product);
            setKeywords(product._keywords);
    }, 1000);
    
}

if(barcode === ""){
    return (
        <div>Enter a barcode to search the product
    
            <form onSubmit={apiRequest}>
                <input type="text"
                       name="barcode"
                       placeholder="8436048475739"
                />
                &nbsp;
                <input type="submit" value="Search product" />
            </form>
            </div>
    )
}
if(data.product_name){
    return (
        <div>Enter a barcode to search the product
    
            <form onSubmit={apiRequest}>
                <input type="text"
                       name="barcode"
                       placeholder="8436048475739"
                />
                &nbsp;
                <input type="submit" value="Search product" />
            </form>
    
            <br></br>
            <img src={data.image_url} width="80"></img>
            <p>  Barcode: {barcode}</p>
            <p>  Name: {data.product_name} </p>
            <p>  Keywords: {keywords + ''} </p>
        </div>
      )
} else if(!data.image_url){
    return (
        <div>Enter a barcode to search the product
    
            <form onSubmit={apiRequest}>
                <input type="text"
                       name="barcode"
                       placeholder="8436048475739"
                />
                &nbsp;
                <input type="submit" value="Search product" />
            </form>
    
            <br></br>
            <p>  This product have no image</p>
            <p>  Barcode: {barcode}</p>
            <p>  Name: {data.product_name} </p>
            <p>  Keywords: {keywords + ''} </p>
        </div>
      )
} else {
    return (
        <div>Enter a barcode to search the product
    
            <form onSubmit={apiRequest}>
                <input type="text"
                       name="barcode"
                       placeholder="8436048475739"
                />
                &nbsp;
                <input type="submit" value="Search product" />
            </form>
    
            <br></br>
            <p>Enter a barcode, or a valid one</p>
            </div>
    )
}
  
}
