import React, { useEffect, useState } from 'react'

export const SearchComponent = () => {

const [barcode, setBarcode] = useState();
const [dataObject, setDataObject] = useState();

const apiRequest = e => {

    e.preventDefault();

    setBarcode(e.target.value);
    console.log("https://world.openfoodfacts.org/api/v0/product/"+{barcode}+".json");
}

const getProduct = async () =>{
    const request = await fetch("https://world.openfoodfacts.org/api/v0/product/8436048475739.json");
    const {product} = await request.json();

    setDataObject(product);
    console.log(product);
    //fa falta 
    //-nom del producte
    //-keywords del producte
    //-imatge del producte
    //console.log('https://world.openfoodfacts.org/api/v0/product/${barcode}.json');
}

useEffect(() => {
    getProduct();
}, []);

  return (
    <div>Enter a barcode to search the product

    <p>{barcode}</p>
        <form onSubmit={apiRequest}>
            <input type="text"
                   name="barcode"
                   placeholder="8436048475739"
            />
            &nbsp;
            <input type="submit" value="Search product" />
        </form>
        <ol>
            {
                dataObject.map()
            }
            <li>  Name: {data.product_name} Keywords: {data.keywords}</li>
        </ol>
    </div>
  )
}
