import React, { useState } from 'react'

export const SearchComponent = () => {

const [barcode, setBarcode] = useState();

const apiRequest = e => {
    setBarcode(e.target.value);

    console.log("send" + e.target.value);
}

  return (
    <div>Enter a barcode to search the product
        <form>
            <input type="text"
                onChange={apiRequest}
                placeholder="8436048475739"
            />
            <button></button>
        </form>
        <p>
            {barcode}
        </p>
    </div>
  )
}
