import React, { useEffect, useState } from 'react'

export const SearchComponent = () => {

const [barcode, setBarcode] = useState();
const [data, setData] = useState([]);
const [keywords, setKeywords] = useState([]);

const apiRequest = e => {

    e.preventDefault();

    setBarcode(e.target.value);
    console.log("https://world.openfoodfacts.org/api/v0/product/"+{barcode}+".json");
}

const getProduct = async () =>{
    const request = await fetch("https://world.openfoodfacts.org/api/v0/product/8436048475739.json");
    const {product} = await request.json();

    setData(product);
    setKeywords(product._keywords)
    console.log(keywords);
}

useEffect(() => {
    getProduct();
}, []);

  return (
    <div>Enter a barcode to search the product

    <p>{barcode}</p>
        <form onSubmit={apiRequest}>
            <input type="text"
                   name="barcode"
                   placeholder="8436048475739"
            />
            &nbsp;
            <input type="submit" value="Search product" />
        </form>

        <br></br>
        
        <img src={data.image_url} width="80"></img>
        <p>  Name: {data.product_name} </p>
        <ul>
            <p>Keywords: </p>
            {
                keywords.map(keyword => {
                    console.log(keyword);
                    return <li key={keyword.id}> Keywords: {keywords + " "} </li>
                })
            }
        </ul>
    </div>
  )
}
