import React, { useState } from "react";

export const Search = () => {
  const [barcode, setBarcode] = useState("");
  const [data, setData] = useState([]);
  const [keywords, setKeywords] = useState([]);
  const [errors, setErrors] = useState("");

  const apiRequest = (e) => {
    e.preventDefault();

    let datos = e.target;
    let search = datos.barcode.value;
    setBarcode(search);

    setTimeout(async () => {
        try{
            const request = await fetch(
                "https://world.openfoodfacts.org/api/v0/product/" + search + ".json"
              );
              const { product } = await request.json();
        
              setData(product);
              setKeywords(product._keywords);
        } catch(error){
            setErrors(error.message);
        }
      
    }, 1000);
  };

  if(errors !== "") {
    return (
      <div>
        <p>Enter a barcode to search the product</p>
        <form className="card card-body" onSubmit={apiRequest}>
          <div className="form-group input-group">
            <input
              className="form-control"
              type="text"
              name="barcode"
              placeholder="8436048475739"
            />
            &nbsp;
            <input
              className="btn btn-primary btn-block"
              type="submit"
              value="Search product"
            />
          </div>
        </form>

        <br></br>
        <p>Enter a barcode, or a valid one</p>
      </div>
    );
  } else if (barcode === "") {
    return (
      <div>
        <p>Enter a barcode to search the product</p>
        <form className="card card-body" onSubmit={apiRequest}>
          <div className="form-group input-group">
            <input
              className="form-control"
              type="text"
              name="barcode"
              placeholder="8436048475739"
            />
            &nbsp;
            <input
              className="btn btn-primary btn-block"
              type="submit"
              value="Search product"
            />
          </div>
        </form>
      </div>
    );
  } else if (data.product_name) {
    return (
      <div>
        <p>Enter a barcode to search the product</p>
        <form className="card card-body" onSubmit={apiRequest}>
          <div className="form-group input-group">
            <input
              className="form-control"
              type="text"
              name="barcode"
              placeholder="8436048475739"
            />
            &nbsp;
            <input
              className="btn btn-primary btn-block"
              type="submit"
              value="Search product"
            />
          </div>
        </form>

        <br></br>
        <div className="col-md-8">
            <div className="card mb-1">
                <div className="card-body">
                    <img src={data.image_url} width="80" alt={data.product_name}></img>
                    <p> Barcode: {barcode}</p>
                    <p> Name: {data.product_name} </p>
                    <p> Keywords: {keywords + ""} </p>
                </div>
            </div>
        </div>
        
      </div>
    );
  } 
};
